var getUserRole =  (name, role) => {
    switch (role) {
        case "admin":
            return `${name} is admin with all access`;
            break; 

            case "subadmin":
                return `${name} is subadmin with access to create/delete courses`;
                break;

                case "testprep":
                    return `${name} is testprep with access to create/delete tests`;
                    break;

                    case "user":
                        return `${name} is user to consume all contents`;
                        break;

                        
    
        default:
            return `${name} is a trail user`
            break;
    }
}

console.log(getUserRole("Saket" , "testprep"));

module.exports = getUserRole;
